/* grafana dashboard='Host Metrics', user='3' */
SELECT t, groupArray((device, c)) AS groupArr FROM ( SELECT (intDiv(toFloat64(TimeUnix) * 1000, (1 * 1000)) * (1 * 1000)) AS t, Attributes['device'] device, anyLastIf(Value, Attributes['direction'] == 'transmit') c from base14.otel_metrics_sum WHERE TimeUnix >= toDateTime64(1752733315,3) AND TimeUnix <= toDateTime64(1752735115,3) AND TimeUnix >= toDateTime64(1752733315,3) AND TimeUnix <= toDateTime64(1752735115,3)
AND MetricName = 'system.network.io' GROUP BY t, device ORDER BY t, device) GROUP BY t ORDER BY t

